const { productTemperature } = data.productTemperatureGrid.reduce((a, b) => {
  if(a.productTemperature >= b.productTemperature) {
    return a
  } else {
    return b
  }
})

if(data.processSteps.Cook && typeof productTemperature === 'number') {
  if(data.cook === 'Full Cook' && typeof data.criticalLimit === 'number') {
    value = productTemperature > data.criticalLimit ? 'Pass' : 'Fail'
  } else if(data.cook === 'Seal | Sear | Partial Cook' && typeof data.criticalLimit1 === 'number') {
    value = productTemperature > data.criticalLimit1 ? 'Pass' : 'Fail'
  } else {
    value = null
  }
} else {
  value = null
}

